const eventosPrincipais = [
  "Coffee Break",
  "Café da Manhã",
  "Almoço",
  "Brunch",
  "Coquetel",
  "Ilha Gastronômica",
  "Serviços Adicionais"
];

async function main() {
  // Limpar tabelas antes de inserir novos dados
  await db.delete(eventMenus);
  await db.delete(menuDishes);
  await db.delete(dishes);
  await db.delete(menus);
  await db.delete(events);

  console.log("[SEED] Criando eventos principais...");
  const eventoIds: Record<string, number> = {};
  for (const nomeEvento of eventosPrincipais) {
    const [evento] = await db.insert(events).values({
      title: nomeEvento,
      description: `Evento do tipo ${nomeEvento}`,
      status: "available",
      eventType: nomeEvento.toLowerCase().replace(/ /g, "_"),
      imageUrl: "https://via.placeholder.com/300x200?text=" + encodeURIComponent(nomeEvento),
      menuOptions: 1
    }).returning();
    eventoIds[nomeEvento] = evento.id;
  }
  console.log("[SEED] Eventos principais criados com sucesso!");

  // 2. Criar menus únicos
  console.log("[SEED] Criando menus únicos...");
  const menusUnicos = Array.from(new Set(data.map(d => d.servico)));
  const menuIds: Record<string, number> = {};
  for (const nomeMenu of menusUnicos) {
    const [menu] = await db.insert(menus).values({
      name: nomeMenu,
      nameEn: nomeMenu,
      description: `Menu especial para ${nomeMenu}`,
      descriptionEn: `Special menu for ${nomeMenu}`,
      price: 1,
      image_url: "https://via.placeholder.com/300x200?text=" + encodeURIComponent(nomeMenu),
    }).returning();
    menuIds[nomeMenu] = menu.id;
  }
  console.log("[SEED] Menus criados com sucesso!");

  // 3. Associar menus aos eventos corretos
  console.log("[SEED] Associando menus aos eventos...");
  for (const nomeMenu of menusUnicos) {
    // Descobrir a qual evento principal pertence esse menu
    let eventoNome = "";
    if (nomeMenu.includes("COFFEE BREAK")) eventoNome = "Coffee Break";
    else if (nomeMenu.includes("CAFÉ DA MANHÃ")) eventoNome = "Café da Manhã";
    else if (nomeMenu.includes("ALMOÇO")) eventoNome = "Almoço";
    else if (nomeMenu.includes("BRUNCH")) eventoNome = "Brunch";
    else if (nomeMenu.includes("COQUETEL")) eventoNome = "Coquetel";
    else if (nomeMenu.includes("ILHA GASTRONÔMICA")) eventoNome = "Ilha Gastronômica";
    else if (nomeMenu.includes("SERVIÇOS ADICIONAIS")) eventoNome = "Serviços Adicionais";
    
    // Verificar se o evento existe antes de associar
    if (eventoNome && eventoIds[eventoNome]) {
      await db.insert(eventMenus).values({
        eventId: eventoIds[eventoNome],
        menuId: menuIds[nomeMenu],
      });
    } else {
      console.log(`[SEED] Aviso: Menu "${nomeMenu}" não foi associado a nenhum evento.`);
    }
  }
  console.log("[SEED] Menus associados aos eventos com sucesso!");

  // 4. Criar pratos vinculados ao menu correto
  console.log("[SEED] Criando pratos vinculados aos menus...");
  let count = 0;
  for (const item of data) {
    await db.insert(dishes).values({
      name: item.prato,
      description: item.prato,
      nameEn: item.prato,
      descriptionEn: item.prato,
      price: 1,
      category: item.categoria,
      categoryEn: item.categoria,
      menuId: menuIds[item.servico],
      imageUrl: "https://via.placeholder.com/300x200?text=" + encodeURIComponent(item.prato)
    });
    count++;
    if (count % 20 === 0) console.log(`[SEED] ${count} pratos inseridos...`);
  }
  console.log(`[SEED] Todos os pratos criados com sucesso! Total: ${count}`);

  console.log("Menus, pratos e eventos populados com sucesso!");
}

